def classify_numbers(numbers):
    """Classify numbers as positive, negative, or zero."""
    positive = []
    negative = []
    zeros = []

    for num in numbers:
        if num &gt; 0:
            positive.append(num)
        elif num &lt; 0:
            negative.append(num)
        else:
            zeros.append(num)

    return positive, negative, zeros

