def word_count(text):
    """Count words in text."""
    words = text.split()
    return len(words)

def char_count(text, exclude_spaces=True):
    """Count characters in text."""
    if exclude_spaces:
        return len(text.replace(" ", ""))
    return len(text)

def title_case(text):
    """Convert text to title case."""
    return " ".join(word.capitalize() for word in text.split())

