6.6.2 Example 2: Mathematical Functions

def factorial(n):
    """Calculate factorial of n."""
    if n == 0 or n == 1:
        return 1
    result = 1
    for i in range(2, n + 1):
        result *= i
    return result

def fibonacci(n):
    """Generate first n fibonacci numbers."""
    if n &lt;= 0:
        return []
    elif n == 1:
        return [0]
    elif n == 2:
        return [0, 1]

    fib_sequence = [0, 1]
    for i in range(2, n):
        next_fib = fib_sequence[i-1] + fib_sequence[i-2]
        fib_sequence.append(next_fib)

    return fib_sequence

def is_prime(n):
    """Check if number is prime."""
    if n &lt; 2:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True

def prime_factors(n):
    """Find prime factors of n."""
    factors = []
    d = 2
    while d * d &lt;= n:
        while n % d == 0:
            factors.append(d)
            n //= d
        d += 1
    if n &gt; 1:
        factors.append(n)
    return factors

