def find_max(numbers):
    """Find maximum number in list."""
    if not numbers:
        return None
    max_num = numbers[0]
    for num in numbers:
        if num &gt; max_num:
            max_num = num
    return max_num

def remove_duplicates(items):
    """Remove duplicates while preserving order."""
    seen = set()
    result = []
    for item in items:
        if item not in seen:
            seen.add(item)
            result.append(item)
    return result

def reverse_string(text):
    """Reverse a string using iteration."""
    reversed_text = ""
    for char in text:
        reversed_text = char + reversed_text
    return reversed_text

def count_vowels(text):
    """Count vowels in text."""
    vowels = "aeiouAEIOU"
    count = 0
    for char in text:
        if char in vowels:
            count += 1
    return count

