9.2 Polymorphism

9.2.1 Method Overriding and Polymorphism

class Shape:
    """Base class for geometric shapes."""

    def __init__(self, name):
        self.name = name

    def area(self):
        """Calculate area - to be implemented by subclasses."""
        raise NotImplementedError("Subclasses must implement area()")

    def perimeter(self):
        """Calculate perimeter - to be implemented by subclasses."""
        raise NotImplementedError("Subclasses must implement perimeter()")

    def describe(self):
        """Describe the shape."""
        return f"This is a {self.name} with area {self.area():.2f} and perimeter {self.perimeter():.2f}"

class Rectangle(Shape):
    """Rectangle implementation."""

    def __init__(self, width, height):
        super().__init__("Rectangle")
        self.width = width
        self.height = height

    def area(self):
        """Calculate rectangle area."""
        return self.width * self.height

    def perimeter(self):
        """Calculate rectangle perimeter."""
        return 2 * (self.width + self.height)

class Circle(Shape):
    """Circle implementation."""

    def __init__(self, radius):
        super().__init__("Circle")
        self.radius = radius

    def area(self):
        """Calculate circle area."""
        import math
        return math.pi * self.radius ** 2

    def perimeter(self):
        """Calculate circle circumference."""
        import math
        return 2 * math.pi * self.radius

class Triangle(Shape):
    """Triangle implementation."""

    def __init__(self, base, height, side1, side2):
        super().__init__("Triangle")
        self.base = base
        self.height = height
        self.side1 = side1
        self.side2 = side2

    def area(self):
        """Calculate triangle area."""
        return 0.5 * self.base * self.height

    def perimeter(self):
        """Calculate triangle perimeter."""
        return self.base + self.side1 + self.side2

# Polymorphism in action
def calculate_total_area(shapes):
    """Calculate total area of multiple shapes."""
    total = 0
    for shape in shapes:
        # Polymorphism: same method call works for all shape types
        total += shape.area()
        print(f"{shape.name}: {shape.area():.2f}")
    return total

def print_shape_info(shapes):
    """Print information about shapes using polymorphism."""
    for shape in shapes:
        # Polymorphism: describe() calls different area() methods
        print(shape.describe())

