9.3.1 Abstract Base Classes

from abc import ABC, abstractmethod

class Vehicle(ABC):
    """Abstract base class for vehicles."""

    def __init__(self, make, model, year):
        self.make = make
        self.model = model
        self.year = year
        self.fuel_level = 100

    @abstractmethod
    def start_engine(self):
        """Abstract method - must be implemented by subclasses."""
        pass

    @abstractmethod
    def stop_engine(self):
        """Abstract method - must be implemented by subclasses."""
        pass

    @abstractmethod
    def accelerate(self):
        """Abstract method - must be implemented by subclasses."""
        pass

    def honk(self):
        """Concrete method - can be used by all vehicles."""
        print("HONK! HONK!")

    def refuel(self):
        """Concrete method - can be used by all vehicles."""
        self.fuel_level = 100
        print("Vehicle refueled")

class Car(Vehicle):
    """Car implementation of Vehicle."""

    def __init__(self, make, model, year, doors):
        super().__init__(make, model, year)
        self.doors = doors
        self.engine_running = False

    def start_engine(self):
        """Start car engine."""
        if not self.engine_running:
            self.engine_running = True
            print(f"Car engine started: Vroom!")
        else:
            print("Engine is already running")

    def stop_engine(self):
        """Stop car engine."""
        if self.engine_running:
            self.engine_running = False
            print("Car engine stopped")
        else:
            print("Engine is already off")

    def accelerate(self):
        """Accelerate the car."""
        if self.engine_running and self.fuel_level &gt; 0:
            self.fuel_level -= 5
            print(f"Car accelerating... Fuel: {self.fuel_level}%")
        else:
            print("Cannot accelerate: engine off or no fuel")

class Motorcycle(Vehicle):
