import json
from datetime import datetime
from typing import Dict, List, Optional

class TaskManager:
    """A simple task management system demonstrating API design patterns."""

    def __init__(self):
        self.tasks: List[Dict] = []
        self.next_id = 1

    def create_task(self, title: str, description: str = "") -&gt; Dict:
        """Create a new task."""
        task = {
            "id": self.next_id,
            "title": title,
            "description": description,
            "completed": False,
            "created_at": datetime.now().isoformat(),
            "updated_at": datetime.now().isoformat()
        }
        self.tasks.append(task)
        self.next_id += 1
        return task

    def get_task(self, task_id: int) -&gt; Optional[Dict]:
        """Get a task by ID."""
        return next((task for task in self.tasks if task["id"] == task_id), None)

    def get_all_tasks(self, completed: Optional[bool] = None) -&gt; List[Dict]:
        """Get all tasks, optionally filtered by completion status."""
        if completed is None:
            return self.tasks.copy()
        return [task for task in self.tasks if task["completed"] == completed]

    def update_task(self, task_id: int, **updates) -&gt; Optional[Dict]:
        """Update a task."""
        task = self.get_task(task_id)
        if task:
            task.update(updates)
            task["updated_at"] = datetime.now().isoformat()
            return task
        return None

    def delete_task(self, task_id: int) -&gt; bool:
        """Delete a task."""
        task = self.get_task(task_id)
        if task:
            self.tasks.remove(task)
            return True
        return False

    def get_statistics(self) -&gt; Dict:
        """Get task statistics."""
        total = len(self.tasks)
        completed = len([t for t in self.tasks if t["completed"]])
        return {
            "total_tasks": total,
            "completed_tasks": completed,
            "pending_tasks": total - completed,
            "completion_rate": (completed / total * 100) if total &gt; 0 else 0
        }

# Demonstrate the task manager
