from enum import Enum

class SensorType(Enum):
    """ã‚»ãƒ³ã‚µãƒ¼ã‚¿ã‚¤ãƒ—"""
    TEMPERATURE = "æ¸©åº¦"
    HUMIDITY = "æ¹¿åº¦"
    PRESSURE = "æ°—åœ§"
    LIGHT = "å…‰"
    MOTION = "å‹•ä½œ"
    SOUND = "éŸ³"
    GAS = "ã‚¬ã‚¹"

@dataclass
class SensorReading:
    """ã‚»ãƒ³ã‚µãƒ¼èª­ã¿å–ã‚Šå€¤"""
    sensor_id: str
    sensor_type: SensorType
    value: float
    unit: str
    timestamp: datetime
